import React, { useState, useRef, useEffect } from 'react';

const MaroofWebsite = () => {
  const [darkMode, setDarkMode] = useState(false);
  const audioRef = useRef(null);

  const toggleDarkMode = () => {
    setDarkMode(!darkMode);
    if (!darkMode) {
      audioRef.current.play();
    } else {
      audioRef.current.pause();
      audioRef.current.currentTime = 0;
    }
  };

  useEffect(() => {
    // Preload the audio
    if (audioRef.current) {
      audioRef.current.load();
    }
  }, []);

  const roles = [
    "SWE Fellow at Headstarter AI",
    "Buildspace S5 Cohort Participant",
    "Remote Intern at Lombard Hill Group",
    "Content Creator",
    "Freelance Developer",
    "3D Game Developer",
    "Animated Movie Creator",
    "Public Speaker",
    "Volunteer in Youth and Educational Events",
    "Computer Applications Student"
  ];

  return (
    <div className={`min-h-screen ${darkMode ? 'bg-gray-900 text-white' : 'bg-[#ebde17] text-black'} flex flex-col items-center justify-center transition-colors duration-500`}>
      <div className="container mx-auto px-4 py-8 text-center relative z-10">
        <h1 className="text-4xl font-bold mb-4">About Maroof Sultana C K</h1>
        <p className="mb-4">
          I'm a Bachelor of Computer Applications student at the University of Mysore, 
          passionate about technology and constantly expanding my skills in various domains.
        </p>
        <h2 className="text-2xl font-semibold mb-2">Who am I?</h2>
        <ul className="list-none mb-8">
          {roles.map((role, index) => (
            <li key={index} className="mb-1">{role}</li>
          ))}
        </ul>
        <p className="mb-8">
          Best,<br />
          Maroof
        </p>
        <div className="space-x-4">
          <a href="mailto:maroof786m7@gmail.com" className="underline">Email</a>
          <a href="https://www.linkedin.com/in/your-linkedin" className="underline">LinkedIn</a>
          <a href="https://sage.buildspace.so/@maroof-sultana-c-k-UerOxUu" className="underline">Sage</a>
          <button onClick={toggleDarkMode} className="underline">
            {darkMode ? 'light-mode.exe' : 'dark-mode.exe'}
          </button>
        </div>
      </div>
      {darkMode && (
        <div className="fixed inset-0 z-0">
          <iframe src='https://my.spline.design/tvatimedoor-1f313c7e134d07de52082aa93ba7a073/' frameBorder='0' width='100%' height='100%'></iframe>
        </div>
      )}
      <audio ref={audioRef} src="/Fred_again_Jozzy-ten.m4a" />
    </div>
  );
};

export default MaroofWebsite;
