import React, { useState, useRef, useEffect } from 'react';

const MaroofWebsite = () => {
  const [darkMode, setDarkMode] = useState(false);
  const audioRef = useRef(null);

  const toggleDarkMode = () => {
    setDarkMode(!darkMode);
    if (!darkMode) {
      audioRef.current.play();
    } else {
      audioRef.current.pause();
      audioRef.current.currentTime = 0;
    }
  };

  useEffect(() => {
    // Preload the audio
    audioRef.current.load();
  }, []);

  const roles = [
    "SWE Fellow at Headstarter AI",
    "Buildspace S5 Cohort Participant",
    "Remote Intern at Lombard Hill Group",
    "Content Creator",
    "Freelance Developer",
    "3D Game Developer",
    "Animated Movie Creator",
    "Public Speaker",
    "Volunteer in Youth and Educational Events",
    "Computer Applications Student"
  ];

  return (
    <div className={`min-h-screen ${darkMode ? 'bg-gray-900 text-white' : 'bg-yellow-500 text-black'} flex flex-col items-center justify-center transition-colors duration-500`}>
      <div className="container mx-auto px-4 py-8 text-center relative z-10">
        <h1 className="text-4xl font-bold mb-4">About Maroof Sultana C K</h1>
        <p className="mb-4">
          I'm a Bachelor of Computer Applications student at the University of Mysore, 
          passionate about technology and constantly expanding my skills in various domains.
        </p>
        <h2 className="text-2xl font-semibold mb-2">Who am I?</h2>
        <ul className="list-none mb-8">
          {roles.map((role, index) => (
            <li key={index} className="mb-1">{role}</li>
          ))}
        </ul>
        <p className="mb-8">
          Best,<br />
          Maroof
        </p>
        <div className="space-x-4">
          <a href="mailto:maroof786m7@gmail.com" className="underline">Email</a>
          <a href="https://www.linkedin.com/in/your-linkedin" className="underline">LinkedIn</a>
          <a href="https://sage.buildspace.so/@maroof-sultana-c-k-UerOxUu" className="underline">Sage</a>
          <a href="https://drive.google.com/file/d/1Ir7aEAFHo2Z33RdDZWpr2A7_VGmydNm8/view?usp=sharing" className="underline" target="_blank" rel="noopener noreferrer">Resume</a>
          <button onClick={toggleDarkMode} className="underline">
            {darkMode ? 'light-mode.exe' : 'dark-mode.exe'}
          </button>
        </div>
      </div>
      {darkMode && (
        <div className="fixed inset-0 z-0 bg-black opacity-50">
          {/* Dark mode background or overlay */}
        </div>
      )}
      <div className="fixed bottom-4 flex justify-center items-center w-full px-4">
        <iframe
          style={{ borderRadius: '12px' }}
          src="https://open.spotify.com/embed/track/5QOBT97OmYCZo1W5u7tRrB?utm_source=generator"
          width="300"
          height="80"
          frameBorder="0"
          allowFullScreen=""
          allow="autoplay; clipboard-write; encrypted-media; fullscreen; picture-in-picture"
          loading="lazy"
          title="Music Player"
        ></iframe>
      </div>
      <audio ref={audioRef} src="/Fred_again_Jozzy-ten.m4a" />
    </div>
  );
};

export default MaroofWebsite;
