import React, { useState, useRef } from 'react';

const MaroofWebsite = () => {
  const [darkMode, setDarkMode] = useState(false);
  const iframeRef = useRef(null);

  const toggleDarkMode = () => {
    setDarkMode(prevDarkMode => !prevDarkMode);
    if (!darkMode) {
      iframeRef.current.contentWindow.postMessage('{"event":"command","func":"playVideo","args":""}', '*');
    } else {
      iframeRef.current.contentWindow.postMessage('{"event":"command","func":"pauseVideo","args":""}', '*');
    }
  };

  const roles = [
    "Blockchain Developer",
    "AI Engineer",
    "Full-Stack Developer",
    "Game Developer",
    "Animation and VFX Artist",
    "Product Manager",
    "Technical Content Creator",
    "Startup Founder/Entrepreneur"
  ];

  return (
    <div className={`min-h-screen ${darkMode ? 'bg-gray-900 text-white' : 'bg-[#ebde17] text-gray-900'} flex flex-col items-center justify-center transition-colors duration-500`}>
      <div className="text-center">
        <h1 className="text-4xl font-bold mb-4">About Maroof Sultana C K</h1>
        <p className="mb-4">
          I'm a Bachelor of Computer Applications student at the University of Mysore, 
          passionate about technology and constantly expanding my skills in various domains.
        </p>
        <h2 className="text-2xl font-semibold mb-2">Future Roles</h2>
        <ul className="list-none mb-8">
          {roles.map((role, index) => (
            <li key={index} className="mb-1">{role}</li>
          ))}
        </ul>
        <p className="mb-8">
          Best,<br />
          Maroof
        </p>
        <div className="space-x-4">
          <a href="mailto:maroof786m7@gmail.com" className="underline">Email</a>
          <a href="https://www.linkedin.com/in/maroof-sultana-c-k-786m777l/" className="underline">LinkedIn</a>
          <a href="https://sage.buildspace.so/@maroof-sultana-c-k-UerOxUu" className="underline">Sage</a>
          <button onClick={toggleDarkMode} className="underline mt-4">
            {darkMode ? 'light-mode.exe' : 'dark-mode.exe'}
          </button>
        </div>
      </div>
      <iframe
        ref={iframeRef}
        width="0"
        height="0"
        src="https://www.youtube.com/embed/iV-7verW0wA?autoplay=1&mute=1"
        frameBorder="0"
        allow="autoplay"
        title="Hidden YouTube Audio"
        style={{ display: 'none' }}
      />
    </div>
  );
};

export default MaroofWebsite;
