import React, { useState, useRef, useEffect } from 'react';

const MaroofWebsite = () => {
  const [darkMode, setDarkMode] = useState(false);
  const audioRef = useRef(null);
  const viewerContainerRef = useRef(null);

  const toggleDarkMode = () => {
    setDarkMode(!darkMode);
    if (!darkMode) {
      audioRef.current.play();
    } else {
      audioRef.current.pause();
      audioRef.current.currentTime = 0;
    }
  };

  useEffect(() => {
    // Preload the audio
    if (audioRef.current) {
      audioRef.current.load();
    }

    // Dynamically add the Spline Viewer script
    const script = document.createElement('script');
    script.type = 'module';
    script.src = 'https://unpkg.com/@splinetool/viewer@1.9.4/build/spline-viewer.js';
    document.body.appendChild(script);

    script.onload = () => {
      // Initialize the Spline viewer when the script is loaded
      const viewer = document.createElement('spline-viewer');
      viewer.setAttribute('url', 'https://prod.spline.design/344ilRsBBw6bzoyD/scene.splinecode');
      viewerContainerRef.current.appendChild(viewer);
    };

    // Cleanup the script and viewer when the component unmounts
    return () => {
      document.body.removeChild(script);
      if (viewerContainerRef.current) {
        viewerContainerRef.current.innerHTML = ''; // Clear the container
      }
    };
  }, []);

  const roles = [
    "SWE Fellow at Headstarter AI",
    "Buildspace S5 Cohort Participant",
    "Remote Intern at Lombard Hill Group",
    "Content Creator",
    "Freelance Developer",
    "3D Game Developer",
    "Animated Movie Creator",
    "Public Speaker",
    "Volunteer in Youth and Educational Events",
    "Computer Applications Student"
  ];

  return (
    <div className={`min-h-screen ${darkMode ? 'bg-gray-900 text-white' : 'bg-blue-500 text-white'} flex flex-col items-center justify-center transition-colors duration-500`}>
      <div className="container mx-auto px-4 py-8 text-center">
        <h1 className="text-4xl font-bold mb-4">About Maroof Sultana C K</h1>
        <p className="mb-4">
          I'm a Bachelor of Computer Applications student at the University of Mysore, 
          passionate about technology and constantly expanding my skills in various domains.
        </p>
        <h2 className="text-2xl font-semibold mb-2">Who am I?</h2>
        <ul className="list-none mb-8">
          {roles.map((role, index) => (
            <li key={index} className="mb-1">{role}</li>
          ))}
        </ul>
        <p className="mb-8">
          Best,<br />
          Maroof
        </p>
        <div className="space-x-4">
          <a href="mailto:maroof786m7@gmail.com" className="underline">Email</a>
          <a href="https://www.linkedin.com/in/your-linkedin" className="underline">LinkedIn</a>
          <a href="https://sage.buildspace.so/@maroof-sultana-c-k-UerOxUu" className="underline">Sage</a>
          <button onClick={toggleDarkMode} className="underline">
            {darkMode ? 'light-mode.exe' : 'dark-mode.exe'}
          </button>
        </div>
      </div>
      {darkMode && (
        <div className="fixed inset-0 z-[-1]">
          <div ref={viewerContainerRef} className="w-full h-full"></div>
        </div>
      )}
      <audio ref={audioRef} src="/path/to/your/audio/file.mp3" /> {/* Replace with your actual audio file path */}
    </div>
  );
};

export default MaroofWebsite;
