import React, { useState, useRef } from 'react';

const MaroofWebsite = () => {
  const [darkMode, setDarkMode] = useState(false);
  const [audioPlaying, setAudioPlaying] = useState(false);

  const toggleDarkMode = () => {
    setDarkMode(prevDarkMode => !prevDarkMode);
    setAudioPlaying(prev => !prev);
  };

  return (
    <div className={`min-h-screen ${darkMode ? 'bg-gray-900 text-white' : 'bg-[#ebde17] text-gray-900'} flex flex-col items-center justify-center transition-colors duration-500`}>
      <div className="text-center">
        <h1 className="text-4xl font-bold mb-4">About Maroof Sultana C K</h1>
        <p className="mb-4">
          I'm a Bachelor of Computer Applications student at the University of Mysore, 
          passionate about technology and constantly expanding my skills in various domains.
        </p>
        <h2 className="text-2xl font-semibold mb-2">Who am I?</h2>
        <ul className="list-none mb-8">
          {[
            "SWE Fellow at Headstarter AI",
            "Buildspace S5 Cohort Participant",
            "Remote Intern at Lombard Hill Group",
            "Content Creator",
            "Freelance Developer",
            "3D Game Developer",
            "Animated Movie Creator",
            "Public Speaker",
            "Volunteer in Youth and Educational Events",
            "Computer Applications Student"
          ].map((role, index) => (
            <li key={index} className="mb-1">{role}</li>
          ))}
        </ul>
        <p className="mb-8">
          Best,<br />
          Maroof
        </p>
        <div className="space-x-4">
          <a href="mailto:maroof786m7@gmail.com" className="underline">Email</a>
          <a href="https://www.linkedin.com/in/your-linkedin" className="underline">LinkedIn</a>
          <a href="https://sage.buildspace.so/@maroof-sultana-c-k-UerOxUu" className="underline">Sage</a>
          <button onClick={toggleDarkMode} className="underline mt-4">
            {darkMode ? 'light-mode.exe' : 'dark-mode.exe'}
          </button>
        </div>
      </div>
      {darkMode && (
        <iframe
          src="https://www.youtube.com/embed/{https://youtu.be/iV-7verW0wA?si=9mj0lz32LbGvv9Az}?autoplay=1&controls=0&mute=1&loop=1&playlist={video-id}" // Replace {video-id} with your YouTube video ID
          width="0" // Hide the video player
          height="0" // Hide the video player
          frameBorder="0"
          allow="autoplay; loop"
          title="YouTube Audio Player"
          className="hidden"
        ></iframe>
      )}
    </div>
  );
};

export default MaroofWebsite;
